#!/usr/bin/perl

$|=1;
our $debug = 1;

use strict; use warnings;
use PDF::Create;

use constant mm => 25.4 / 72;
use constant in => 1 / 72;
use constant pt => 1;

my $pdf = PDF::Create->new(
    'filename'     => "$0.pdf",
    'Author'       => 'MANWAR',
    'Title'        => 'My First PDF',
    'CreationDate' => [ localtime ]
);

my $root = $pdf->new_page('MediaBox' => $pdf->get_page_size('A4'));
my $page = $root->new_page;
my $font = $pdf->font('BaseFont' => 'Helvetica');

# $x, $y, $width, $height
$page->rectangle(30, 780, 535, 40);
$page->setrgbcolor(0,1,0);
$page->fill;

$page->setrgbcolorstroke(1,0,0);
$page->line(30, 778, 565, 778);

$page->setrgbcolor(0,0,1);
$page->string($font, 15, 102, 792, 'MANWAR - PDF::Create');

my $text = very_long_text();
my $lines      = block_text({
    page       => $page,
    font       => $font,
    text       => $text,
    font_size  => 10,
    text_color => [0,0,1],
    line_width => 535,
    start_y    => 780,
    end_y      => 60,
    'x'        => 30,
    'y'        => 750,
});

$pdf->close;

#
#
# METHODS

sub _get_text ($$) {
    my ($words, $num_words) = @_;

    if (scalar @$words < $num_words) { die @_ };

    return join(' ', map { $$words[$_] // die "wanted $_+1, have ".scalar(@$words) } (0..($num_words-1)));
}

sub block_text {
    my ($params) = @_;

    my $page       = $params->{page};
    my $font       = $params->{font};
    my $text       = $params->{text};
    my $font_size  = $params->{font_size};
    my $text_color = $params->{text_color};
    my $line_width = $params->{line_width};
    my $start_y    = $params->{start_y};
    my $end_y      = $params->{end_y};
    my $x          = $params->{x};
    my $y          = $params->{y};

    my @words = split(/ /, $text);
    my $lines = [];
    my $line  = shift @words;

    my $avg_word_length = 5;
    my $size_of_space   = $page->string_width($font, ' ') * $font_size;

    while (@words) {
        my $num_words    = 1;
        my $string_width = 0;

        my $space_width = undef;

        while (1) {
            $string_width = $page->string_width($font, _get_text(\@words, $num_words)) * $font_size;

            # Shorter, try one more word
            if ($string_width + $size_of_space < $line_width) {
                if (scalar(@words) > $num_words) {
                    $num_words++;
                    next;
                }
            }

            last if ($num_words == 1);

            # Longer, chop a word off, then space accordingly
            if ($string_width + $size_of_space > $line_width || scalar(@words) == $num_words) {
                unless (scalar(@words) == $num_words) {
                    $num_words--;
                }
                $string_width = $page->string_width($font, _get_text(\@words, $num_words)) * $font_size;
                $space_width = ($line_width - $string_width) / $num_words;
                last;
            }
        }
        push @$lines, { text => _get_text(\@words, $num_words), space_width => $space_width };

        splice(@words, 0, $num_words);
    }

    # Forget justification on the last line
    # or         it         ends         up         like         this.
    $lines->[-1]->{'space_width'} = undef;

    foreach my $line (@$lines) {
        $page->string(
            $font, $font_size, $x, $y, $line->{'text'},
            undef, undef, $line->{'space_width'},
        );

        if ($y <= $end_y) {
            $y = $start_y;
            $page = $root->new_page;
            $page->setrgbcolor(@$text_color);
        }
        else {
            $y -= int($font_size * 1.5);
        }
    }
}

